    @Test
    /**
     * Tests a suffix insertion BioBrick assembly of two plasmids Example puts
     * Ptet in front of an rbs.luxI.dblterm cassette
     */
    public void BioBrickAssemblyTest() throws Exception {
        ParseConstructionFile pCF = new ParseConstructionFile();
        pCF.initiate();

        String text = "digest pSB3C6-r0040 with SpeI,PstI	(1, lefty)\n"
                + "digest pSB1AK3-J1610 with XbaI,PstI	(0, righty)\n"
                + "ligate lefty,righty	(lig)\n"
                + "transform lig	(Mach1, Spec)\n"
                + "\n"
                + ">pSB3C6-r0040\n"
                + "\n"
                + "\n"
                + ">pSB1AK3-J1610\n"
                + "";
        ConstructionFile CF = pCF.run(text);
        SimulateConstructionFile simulateConstructionFile = new SimulateConstructionFile();
        ConstructionFile outputConstructionFile = simulateConstructionFile.run(CF, new HashMap<>());
        Polynucleotide product = outputConstructionFile.getSequences().get(outputConstructionFile.getPdtName());
        String expectSeq = "";
        Polynucleotide expected = new Polynucleotide(expectSeq, true);
        assert (expected.equals(product));

    }

    @Test
    /**
     * Does the example in the first tutorial:
     * https://openwetware.org/wiki/Arking:JCAOligoTutorial1b
     */
    public void PCRCutAndPasteTest() throws Exception {
        ParseConstructionFile pCF = new ParseConstructionFile();
        pCF.initiate();

        String text = "PCR papC-F/papC-R on S.ven.      (pcrpdt)\n"
                + "Digest pcrpdt with BglII,XhoI,   (1, pcrdig)\n"
                + "Digest pBca9145-Bca1089 with BglII,XhoI (1, vectdig)\n"
                + "Ligate pcrdig,vectdig             (pBca9145-papC)\n"
                + "-----------------------------------------\n"
                + ">papC-F   Cloning of papC\n"
                + "ccaaaAGATCTatgagcggcttcccccgcag\n"
                + ">papC-R   Cloning of papC\n"
                + "gctagCTCGAGttaGGATCCtcatcggtccttctcgccttc\n"
                + ">S.ven.  papC region of S. venezuaelae\n"
                + "\n"
                + ">pBca9145-Bca1089\n"
                + "";
        ConstructionFile CF = pCF.run(text);
        SimulateConstructionFile simulateConstructionFile = new SimulateConstructionFile();
        ConstructionFile outputConstructionFile = simulateConstructionFile.run(CF, new HashMap<>());
        Polynucleotide product = outputConstructionFile.getSequences().get(outputConstructionFile.getPdtName());
        String expectSeq = "";
        Polynucleotide expected = new Polynucleotide(expectSeq, true);
        assert (expected.equals(product));
    }

    @Test
    /**
     * Does the example in the second tutorial:
     * https://openwetware.org/wiki/Arking:JCAOligoTutorial2b
     */
    public void SOECutAndPasteTest() throws Exception {
        ParseConstructionFile pCF = new ParseConstructionFile();
        pCF.initiate();

        String text = "PCR ca1110F,ca1111R on pBACr899     (A)\n"
                + "PCR ca1111F,ca899R on pBACr899      (B)\n"
                + "PCR ca1110F/ca899R on A,B           (pcrpdt)\n"
                + "Digest pcrpdt with EcoRI,BamHI      (1, pcrdig)\n"
                + "Digest pBca1100 with EcoRI,BamHI     (1, plasdig)\n"
                + "Ligate pcrdig,plasdig               (pBca1100-Bca1111)\n"
                + " ---------------------------------------------------\n"
                + ">ca1110F  Forward EcoRI for Biobrick extreme variant of nahR-Psal  \n"
                + "ctctggaattcatgAGATCTGCGATCCCGCGAAGAACC\n"
                + ">ca1111F  Removing the BglII site in nahR  \n"
                + "catgaagtagatTtcgccaatgtc\n"
                + ">ca1111R  Removing the BglII site in nahR\n"
                + "gacattggcgaAatctacttcatg\n"
                + ">ca899R   Reverse BamHI for nahR promoter  \n"
                + "GCAAAggatccTCTATGGTACTCGTGATGGC\n"
                + ">pBACr899\n"
                + "\n"
                + ">pBca1100\n"
                + "";
        ConstructionFile CF = pCF.run(text);
        SimulateConstructionFile simulateConstructionFile = new SimulateConstructionFile();
        ConstructionFile outputConstructionFile = simulateConstructionFile.run(CF, new HashMap<>());
        Polynucleotide product = outputConstructionFile.getSequences().get(outputConstructionFile.getPdtName());
        String expectSeq = "";
        Polynucleotide expected = new Polynucleotide(expectSeq, true);
        assert (expected.equals(product));
    }

