//    @Test
    /**
     * Tests PCR on the complete E. coli genome
     */
    public void MG1655GenomeTest() throws Exception {
        ParseConstructionFile pCF = new ParseConstructionFile();
        pCF.initiate();

        //Inject the genome
        String data = FileUtils.readResourceFile("constructionfile/data/MG1655_sequence.txt");
        String[] lines = data.split("\\r|\\r?\\n");
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < lines.length; i++) {
            sb.append(lines[i]);
        }
        Map<String, Polynucleotide> extras = new HashMap<>();
        extras.put("MG1655", new Polynucleotide(sb.toString(), true));

        String text = "PCR araC1,araC2 on MG1655	     (pcrpdt)\n"
                + "Digest pcrpdt  with BamHI,EcoRI     (2, ins)\n"
                + "Digest pBca9145-Bca1089  with BamHI,EcoRI   (2, vec)\n"
                + "Ligate ins,vec                      (pAraC)\n"
                + "---------------------------------------------------------\n"
                + ">araC1\n"
                + "ccataGAATTCatgAGATCTatggctgaagcgcaaaatgatccc\n"
                + ">araC2\n"
                + "cagtcGGATCCttatgacaacttgacggctacatc\n"
                + ">pBca9145-Bca1089\n"
                + "";
        ConstructionFile CF = pCF.run(text);
        SimulateConstructionFile simulateConstructionFile = new SimulateConstructionFile();
        ConstructionFile outputConstructionFile = simulateConstructionFile.run(CF, extras);
        Polynucleotide product = outputConstructionFile.getSequences().get(outputConstructionFile.getPdtName());
        System.out.println("seq\n" + product.getSequence());
        String expectSeq = "";
        Polynucleotide expected = new Polynucleotide(expectSeq, true);
        assert (expected.equals(product));
    }
